mapper.insert:

insert into sys_button (button_name, button_code, button_type, 
  button_url, button_icon, menu_name, 
  menu_code, sys, remark, 
  create_time, update_time, record_version
  )
values (#{buttonName,jdbcType=VARCHAR}, #{buttonCode,jdbcType=VARCHAR}, #{buttonType,jdbcType=VARCHAR}, 
  #{buttonUrl,jdbcType=VARCHAR}, #{buttonIcon,jdbcType=VARCHAR}, #{menuName,jdbcType=VARCHAR}, 
  #{menuCode,jdbcType=VARCHAR}, #{sys,jdbcType=VARCHAR}, #{remark,jdbcType=VARCHAR}, 
  #{createTime,jdbcType=TIMESTAMP}, #{updateTime,jdbcType=TIMESTAMP}, #{recordVersion,jdbcType=BIGINT}
  )

mapper.select:

select 

from sys_button


     and id = #{record.id,jdbcType=BIGINT}


     and button_name = #{record.buttonName,jdbcType=VARCHAR}


     and button_code = #{record.buttonCode,jdbcType=VARCHAR}


     and button_type = #{record.buttonType,jdbcType=VARCHAR}


     and button_url = #{record.buttonUrl,jdbcType=VARCHAR}


     and button_icon = #{record.buttonIcon,jdbcType=VARCHAR}


     and menu_name = #{record.menuName,jdbcType=VARCHAR}


     and menu_code = #{record.menuCode,jdbcType=VARCHAR}


     and sys = #{record.sys,jdbcType=VARCHAR}


     and remark = #{record.remark,jdbcType=VARCHAR}


     and create_time = #{record.createTime,jdbcType=TIMESTAMP}


     and update_time = #{record.updateTime,jdbcType=TIMESTAMP}


     and record_version = #{record.recordVersion,jdbcType=BIGINT}


limit 1


