mapper.select:

select 

from sys_menu


     and id = #{record.id,jdbcType=BIGINT}


     and menu_name = #{record.menuName,jdbcType=VARCHAR}


     and menu_code = #{record.menuCode,jdbcType=VARCHAR}


     and menu_path = #{record.menuPath,jdbcType=VARCHAR}


     and menu_url = #{record.menuUrl,jdbcType=VARCHAR}


     and menu_icon = #{record.menuIcon,jdbcType=VARCHAR}


     and menu_level = #{record.menuLevel,jdbcType=VARCHAR}


     and menu_type = #{record.menuType,jdbcType=VARCHAR}


     and parent_code = #{record.parentCode,jdbcType=VARCHAR}


     and menu_order = #{record.menuOrder,jdbcType=BIGINT}


     and hidden = #{record.hidden,jdbcType=TINYINT}


     and enabled = #{record.enabled,jdbcType=TINYINT}


     and sys = #{record.sys,jdbcType=VARCHAR}


     and create_time = #{record.createTime,jdbcType=TIMESTAMP}


     and update_time = #{record.updateTime,jdbcType=TIMESTAMP}


     and record_version = #{record.recordVersion,jdbcType=BIGINT}


limit 1


