mapper.select:

select 

from sys_user


     and id = #{record.id,jdbcType=BIGINT}


     and nick_name = #{record.nickName,jdbcType=VARCHAR}


     and user_name = #{record.userName,jdbcType=VARCHAR}


     and password = #{record.password,jdbcType=VARCHAR}


     and phone_num = #{record.phoneNum,jdbcType=VARCHAR}


     and birthday = #{record.birthday,jdbcType=VARCHAR}


     and address = #{record.address,jdbcType=VARCHAR}


     and tel_phone = #{record.telPhone,jdbcType=VARCHAR}


     and email = #{record.email,jdbcType=VARCHAR}


     and sex = #{record.sex,jdbcType=CHAR}


     and is_first_login = #{record.isFirstLogin,jdbcType=VARCHAR}


     and enabled = #{record.enabled,jdbcType=TINYINT}


     and create_time = #{record.createTime,jdbcType=TIMESTAMP}


     and update_time = #{record.updateTime,jdbcType=TIMESTAMP}


     and remark = #{record.remark,jdbcType=VARCHAR}


     and record_version = #{record.recordVersion,jdbcType=BIGINT}


limit 1


